import React,{useState} from "react";
import "./PredictAirConditioner.css"
export function PredictAirConditioner(){
    let [data,setData]=useState([]);
    let [load,isLoading]=useState(false);
    let [prediction,setPredict]=useState(false);
    async function processData(data){
        isLoading(true);
        let objec={compactness:data[0],surface_area:data[1],wall_area:data[2],roof_area:data[3],overall_height:data[4],orientation:data[5],glazing_area:data[6],glazing_area_distribution:data[7]}
        await fetch('/airCon',{method:"POST",headers:{'Content-Type':'application/json'},body:JSON.stringify(objec)}).then(
            response=>response.json()
        ).then(a=>setPredict(a)).catch(e=>console.log(e));
    }
    function handleSubmit(e){
        e.preventDefault();
        let formData =new FormData(e.currentTarget);
        let data =Object.fromEntries(formData)
        let goodvalue=Object.values(data)[0];
        setData(a=>[...a,Number(goodvalue)]);
        
    }
    if(load &&!prediction){
        return (
            <div id="loading">
            <h1 id="h1Loading">
            <span class="let1">l</span>  
            <span class="let2">o</span>  
            <span class="let3">a</span>  
            <span class="let4">d</span>  
            <span class="let5">i</span>  
            <span class="let6">n</span>  
            <span class="let7">g</span>  
          </h1>
          </div>
        )
    }
    if(data.length===8 && prediction===false){
        processData(data);
    }
    if(prediction!==false){
        
        return (
            <div id="results">
                <h1 className="beauty" id="heatTite"style={{'font-size':'67px'}}>The Results</h1>
                <h1 className="testh1" id="heatTitle">Heat Load: {prediction["Heating"]} kW</h1>
                <h3 className="testh3" id="heat">Accuracy Of Prediction: {prediction["AccuracyHeat"]}</h3>
                <h1 className="testh1" id="coldTitle">Cool Load: {prediction["Cooling"]} kW</h1>
                <h3 className="testh3" id="cold">Accuracy Of Prediction: {prediction["AccuracyCool"]}</h3>
                <div class="box">
	            <a class="button" href="#popup1">How To Use The Information</a>
                </div>
                <div id="popup1" class="overlay">
	        <div class="popup">
		    <h2>Useful Information</h2>
		    <a class="close" href="#">&times;</a>
		    <div class="content">
		    	<h3>Here Is How You Determine How Much You Should Use These Predictions: </h3><br/>
                <h5>An Accuracy Score Of 0 Means You Should Throw These Predictions Away.</h5> 
                <h5>An Accuracy Score Of 1 Means You Should Really Think About Using These Predictions.</h5>
                <br/>
                <br/>
                <h3>What Is A Heating Load?</h3>
                <p>The Heating Load Represents The Kilowatt Capacity Or The Amount Of Heating Capacity Required To Maintain A Desired Indoor Temperature Within A Building Or Space. In Other Words, It Tells Your HVAC System How Much Heat Must Be Added To Sustain An Indoor Temperature And Counteract Heat Loss.</p>
                <h3>What Is A Cooling Load?</h3>
                <p>The Cooling Load Represents The Amount Of Cooling Capacity Required To Maintain A Desired Indoor Temperature Within A Building Or Space. In Other Words, It Tells Your HVAC System How Much Heat Must Be Taken Out To Sustain A Cold Indoor Temperature And Counteract Heat Flow.</p>
            </div>
    	    </div>
            </div>
            </div>
        )
    }
    if(data.length===0){
        return (
            <div className="questionCss">
                <form onSubmit={handleSubmit}>
                <label className="formLabel" htmlFor="compactness">What Is The Compactness Of Your House?</label>
                <input className="formInput" max="1"step="any" min="0"type="number" required name="compactness" id="compactness"/>
                <button className="formButton" type="submit">Submit</button>
                </form>
                <h2 className="formHead">What Is The Compactness Of A House?</h2>
                <h5 className="formText">House Compactness Refers To How Efficient The Space Use Of A House Is. It Is Calculated By Dividing The Volume Of Interior Space With The Exterior Surface Of A House.</h5>
                <img className="formImage" src="data:image/jpeg;base64,"/>
                <h5 className="formText">A Compactness Of 1 Indicates A Super Compact House While A Lower Compactness Represents Less Efficient Use Of Space.</h5>
            </div>
        );
    }
    if(data.length===1){
        return (
            <div className="questionCss">
                <form onSubmit={handleSubmit}>
                <label className="formLabel" htmlFor="surfaceArea">What Is The Surface Area Of Your House In Meters Squared?</label>
                <input className="formInput" min="0"type="number"step="any" required name="surfaceArea" id="surfaceArea"/>
                <button className="formButton" type="submit">Submit</button>
                </form>
                <h2 className="formHead">What Is The Surface Area Of A House?</h2>
                <h5 className="formText">House Surface Area Refers To The Total External Area Of The Walls, Roof, and Other External Elements Of A House.</h5>
            </div>
        );
    }
    if(data.length===2){
        return (
            <div className="questionCss">
                <form onSubmit={handleSubmit}>
                <label className="formLabel" htmlFor="wallArea">What Is The Wall Area Of Your House In Meters Squared?</label>
                <input className="formInput" min="0"type="number"step="any" required name="wallArea" id="wallArea"/>
                <button className="formButton" type="submit">Submit</button>
                </form>
                <h2 className="formHead">What Is The Wall Area Of A House?</h2>
                <h5 className="formText">Wall Area Refers To The External Surface Area Of All The Exterior Walls In A House.</h5>
                <img className="formImage" src="https://www.liveabout.com/thmb/5rMDCnkihPGtK-Xn16LjiPjy_f4=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/vinyl-siding-56a1bcb95f9b58b7d0c224e2.jpg"/>
            </div>
        );
    }
    if(data.length===3){
        return (
            <div className="questionCss">
                <form onSubmit={handleSubmit}>
                <label className="formLabel" htmlFor="roofArea">What Is The Roof Area Of Your House In Meters Squared?</label>
                <input className="formInput"min="0"type="number" step="any" required name="roofArea" id="roofArea"/>
                <button className="formButton" type="submit">Submit</button>
                </form>
                <h2 className="formHead">What Is The Roof Area Of A House?</h2>
                <h5 className="formText">Roof Area Refers To The External Surface Area Of All The Roof Surfaces That Cover The Building.</h5>
                <img className="formImage" src="data:image/jpeg;base64,"/>
            </div>
        );
    }
    if(data.length===4){
        return (
            <div className="questionCss">
                <form onSubmit={handleSubmit}>
                <label className="formLabel"htmlFor="height">What Is The Overall Height Of Your House In Meters?</label>
                <input className="formInput"min="0"type="number" step="any" required name="height" id="height"/>
                <button className="formButton" type="submit">Submit</button>
                </form>
                <h2 className="formHead">What Is The Overall Height Of A House?</h2>
                <h5 className="formText">The Overall Height Refers To The Distance From The Lowest Point Of Your House Foundation Or Ground Level To The Highest Point Of The Roof. The Highest Point Includes Any Structural Element, Roof Strucutures, And Architectural Featrues.</h5>
            </div>
        );
    }
    if(data.length===5){
        return (
            <div className="questionCss">
                <label className="formLabel">What Is The Orientation Of Your House?</label>
                <button className="formButton" onClick={()=>setData(a=>[...a,2])}>North</button>
                <button className="formButton" onClick={()=>setData(a=>[...a,3])}>East</button>
                <button className="formButton" onClick={()=>setData(a=>[...a,4])}>South</button>
                <button className="formButton" onClick={()=>setData(a=>[...a,5])}>West</button>
                <h2 className="formHead">What Is The Orientation?</h2>
                <h5 className="formText">The Orientation Refers To The Direction The House Is Facing Relative To The Four Cardinal Directions. The Front Of A House Typically Refers To The Side Of The House Where The Front Door Is.</h5>
            </div>
        );
    }
    if (data.length===6){
        return(
            <div className="questionCss">
            <form onSubmit={handleSubmit}>
            <label className="formLabel"htmlFor="glazingArea">What Is The Glazing Area Of Your Houses?</label>
            <input className="formInput"min="0" max="1"type="number" step="any"required name="glazingArea" id="glazingArea"/>
            <button className="formButton" type="submit">Submit</button>
            </form>
            <h2 className="formHead">What Is The Glazing Area?</h2>
            <h5 className="formText">The Glazing Area Refers To The Proportion Of The Combined Area Of All Translucent And Glazed Surfaces, Like Skylights, Windows, And Glass Doors, With The External Wall Surface Area.</h5>
            <img className="formImage" src="data:image/jpeg;base64,"/>
            <h5 className="formText">The Glazing Area Is Represented As A Value Ranging From 0-1, And It Is Calulated By Dividng The External Surface Area Of All Glazed Surfaces By The External Wall Area.</h5>
        </div>
        );
    }
    if (data.length===7){
        return(
            <div className="questionCss">
            <label className="formLabel"htmlFor="glazingAreaDistribution">What Is The Glazing Area Distribution Of Your House?</label>
            <button className="formButton" onClick={()=>setData(a=>[...a,1])}>Uniform</button>
            <button className="formButton" onClick={()=>setData(a=>[...a,2])}>North</button>
            <button className="formButton" onClick={()=>setData(a=>[...a,3])}>East</button>
            <button className="formButton" onClick={()=>setData(a=>[...a,4])}>South</button>
            <button className="formButton"onClick={()=>setData(a=>[...a,5])}>West</button>
            <h2 className="formHead">What Is The Glazing Area Distribution?</h2>
            <h5 className="formText">The Glazing Area Distribution Is The Observation Of Which Cardinal Side Of Your House Has The Nighest Percentage Of The Total Glazed Surface Area. It Basically Tells You Which Side Of The House Has The Most The Glazed Surface. If The Cardinal Sides Share A Relatively Even Share Of The Glazed Surface Area, The Distribution Can Be Considered Uniform.</h5>
        </div>
        );
    }
}